css:
/*pc*/@media (min-width: 601px){
  .top-bar {
    background: #777e7a;
    width: 100%;
}
 .top-bar__nav {color: #ffffff;background:#777e7a;font-size:15px;}
  .d-lg-inline {display: none;}
  .header__logo {max-height: 130px;max-width: 300px;}
.header {background: #ffffff;border-bottom: 1px solid #dddddd;}
  .header__search-input {
    border-radius: 40px;
    font-size: 20px;
     border: 1px solid #d5d5d5;
}
  .header__search-btn {
    color: #b7b7b7;
    font-size: 30px;
}
  h2 {font-size:18px; font-weight: 500;}
    .col-12.col-xl.order-lg-first p{ color:#4e4e4e;font-size:18px;}
  .footer__credits{font-size: 15px;}
  .top-bar__page-links{font-size:15px;}
  .header__nav a {text-transform: uppercase;font-size: 18px;
font-weight: 700;background: transparent;color:#212529;}
   .header__nav a:hover {background: transparent;font-size: 18px;font-weight:700;
 text-decoration: none;color:#75DA60;}
h1 {
    font-size: 1.8rem;
}
.product__name{color:#212529;font-size:24px;font-weight: 700;}
  .header__nav { 
    margin-top: -30px;}
  .footer__links {
    font-size: 74%;
}
}

 /*mobile*/@media (max-width: 600px) {h1 {
    font-size: 1.4rem;
}.product__name{color:#212529;font-size:18px;font-weight: 700;}
}

/*responsivo*/
.a {
    color: #7e7e7e;
    text-decoration: none;
}
.card-footer {
    background-color: #eeffeb;
    border-top: 1px solid rgba(0,0,0,.125);
    padding: .75rem 1.25rem;
}
.i-check{color:#fff;}
.footer {background:rgb(191,255,178);
background: -moz-linear-gradient(180deg, rgba(191,255,178,1) 0%, rgba(171,250,155,1) 35%, rgba(117,218,96,1) 100%);
background: -webkit-linear-gradient(180deg, rgba(191,255,178,1) 0%, rgba(171,250,155,1) 35%, rgba(117,218,96,1) 100%);
background: linear-gradient(180deg, rgba(191,255,178,1) 0%, rgba(171,250,155,1) 35%, rgba(117,218,96,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#bfffb2",endColorstr="#75da60",GradientType=1);}
.info-bar {background: #38A667;}
.ul.footer__links{color:#38A667;font-size: 21px;font-weight: 500;}
.footer__links i {color: #38A667;}
.list-unstyled { color:#38A667;font-size: 18px;font-weight: 500;}
#footer-pages{ color:#38A667; font-size: 24px;font-weight: 500;}
.footer__title {color: #38A667;font-weight: 700;font-size:21px;}
.footer__store {color: #38A667;font-size: 21px;}
.col-12.col-xl.order-lg-first p {color: #607368;font-size: 18px;}
.pb-lg-4{padding-bottom: 0rem;}
.i-whatsapp{color:#b8efd0;}
.header__toggler-icon {color: #38A667;}
.i-user{color: #8b8b8b;}
.i-shopping-bag{color:#75DA60;}
.i-chevron-right{display:none;}
.badge-primary {
    background-color: #38A667;
    color: #fff;
}
 .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}
.d-lg-inline {display: none! important;}
.ecom-credits {background: #ebebeb;}
.menu__item {align-items: center;font-size:18px;
    border-bottom: var(--border-width) solid #ffffff;
    font-weight: 700;background: transparent;color:#212529;}
.menu__item:hover {font-weight: 700;background:#75DA60;color:#fff;font-size:18px;
}
.product-card__offer-stamp {
    background-color: #bf0000;
  border-radius: 20px;}
.product-card__name{ color:#747474;font-size:16px;font-weight: 700;}
.prices__value{color:#38A667;font-size:20px;font-weight: 700;}
.prices__installments{ color:#75DA60;}
.products-carousel__title:before {
  background: #75DA60;}
.products-carousel__title{color:#38A667;}
.html-clearfix{color:#212529;font-size:16px;font-weight: 400;}
.product__short-stock{display:none;}
.app__footer {
    background: #ebebeb;border-top: var(--spacer-1) solid #f8f9fa;
    border-top: var(--spacer-1) solid #e3e3e3;
}
.btn-primary:hover {
    background-color:#529943;
    border-color: #529943;
    color: #fff;
}
.btn-primary{
    background-color: #38A667;
    border-color: #38A667;
    color: #fff;
}
.btn-success {
    background-color: #529943;
    border-color:#529943;
    color: var(--white);
}
.btn-outline-secondary {
    border-color: #38A667;
    color: #38A667;
}
.btn-outline-secondary:hover{
    background: #38A667;
    color: #fff;
}
.card-header {
  background-color: #306025;}
.product__favorite i {
    color: #41a72c;
    opacity: .4;
}
.btn-success:hover{
    background-color: #38A667;
    border-color: #38A667;

}
.i-heart{color: #38A667;}
.list-group-item.active {
    background-color: #bf0000;
    border-color: #bf0000;
    color: #ffffff;
    z-index: 2;
}
.btn-sm {
        font-size: 16px;
}

.btn-light {
    background-color: #38A667;
    border-color: #38A667;
    padding: 5px 60px;
}
.prices:not(.prices--big) .prices__installments {
    font-weight: 700;
}
.prices--big {
    font-size: 18px;
}
#ws_icon_chat img {
    width: 80px !important;
}
#ws_icon_chat {
    position: fixed;
    bottom: 10px;
    left: 15px;
    z-index: 2000;
    cursor: pointer;
    outline: none !important;
}
}
